mapper.select:
select * from ( select jh.jhid jhid, sb.sbid sbid, zj.zjid zjid, jh.hybh hybh, decode(sb.hymc, null, jh.hymc, sb.hymc) hymc, decode(sb.jxrq_ks, null, jh.jxrq_ks, sb.jxrq_ks) jxrq_ks, decode(sb.jxrq_js, null, jh.jxrq_js, sb.jxrq_js) jxrq_js, decode(sb.dd, null, jh.dd, sb.dd) dd, decode(sb.jfly, null, jh.jfly, sb.jfly) jfly, decode(sb.hylx, null, jh.hylx, sb.hylx) hylx,
decode(sb.sbid, null, '0', '1') sfzxjh, /*是否执行计划*/ decode(zj.zjid, null, '0', '1') sfzj /*是否总结*/
from t_hy_jh jh left join t_hy_shenb sb on jh.jhid = sb.jhid left join t_hy_zj zj on sb.sbid = zj.sbid )
and hybh like #{hybhLike,jdbcType=VARCHAR}
and hymc like #{hymcLike,jdbcType=VARCHAR}
and hylx = #{param.hylx,jdbcType=VARCHAR}
order by ${orderCol} ${orderType}
