mapper.select:
select code as "code" , name as "name"
from t_dm_gb t
  where code in (
    select gjdm from t_xm_gjdq xm where xm.xmid = #{xmid}
    )
     and  valid = '1'

select xmbh,xmmc,xh,xm,unit_name,sznj,zjid,file_id,zymc,zzje, decode(xmlx,'01','临床实习','02','假期访学','03','攻读学位','04','课程学习','99','其它','未知') xmlx, F_GetTableText('t_dm_jljgmc', 'code', 'name', jljgmc) jljgmc, F_GetTableText('t_dm_gb', 'code', 'name', jlgjdqm) jlgjdqm, F_GetTableText('t_dm_jfly', 'code', 'name', jfly) jfly,xmkssj,smjssj,xmjlts,rxn,xmcc from (
select xm.xmbh xmbh, sq.xmmc xmmc, sq.xh xh, sq.xm xm, sq.zymc zymc, sq.jlgjdqm jlgjdqm, sq.zzje zzje, substr(sq.rxn,3,2) rxn,
xs.unit_name unit_name, xs.sznj sznj,
xm.xmlx xmlx, xm.jljgmc jljgmc, xm.jfly jfly, xm.xmkssj xmkssj, xm.smjssj smjssj, xm.xmjlts xmjlts, xm.xmcc xmcc,
bg.zjid zjid, bg.file_id file_id
from t_xm_xssqjl sq inner join t_xm xm on sq.xm_id = xm.xm_id
left join (select xh, sznj, yxsh, unit.name unit_name from v_bks_xjjbsjxx xs1 inner join v_uicm_unit unit on xs1.yxsh = unit.unit_id union all select xh, sznj, yxsh, unit.name unit_name from v_yjs_xjjbsjxx xs2 inner join v_uicm_unit unit on xs2.yxsh = unit.unit_id ) xs on sq.xh = xs.xh
left join t_xm_jlzjbg bg on bg.sqjl_id = sq.sqjl_id
)
and xmmc like #{xmmcLike,jdbcType=VARCHAR}
and xm like #{xmLike,jdbcType=VARCHAR}
and xmcc = #{xmcc,jdbcType=VARCHAR}
and xmlx = #{xmlx,jdbcType=VARCHAR}
and jlgjdqm = #{jlgjdqm,jdbcType=VARCHAR}
and xmkssj <= #{xmkssj,jdbcType=TIMESTAMP}
and smjssj >= #{smjssj,jdbcType=TIMESTAMP}
order by ${orderCol} ${orderType}

select xm_id, F_GetTableText('t_dm_xmlx', 'code', 'name', xmlx) xmlx, (select wm_concat(gb.name) from t_xm_gjdq t inner join T_DM_GB gb on gb.code = t.gjdm where t.XMID =XM_ID) gbdq, xmzm, glxmmcid, xmmc, xmbh, xmkssj, smjssj, xmjlts, F_GetTableText('t_dm_xmcc', 'code', 'name', xmcc) xmcc, jfly, zjje, xmgk, xmzzjh, fybz, zysm, sfxzrs, jhrs, F_GetTableText('t_dm_jljgmc', 'code', 'name', jljgmc) jljgmc, ccxz, xmnjxz, yyyq, xmzyxz, gsyxdm, gsyxmc, gsxsdm, gsxsxm, operator_code, bmjzsj, decode(status,'01','暂存','02','已发布','03','允许申报','04','申报结束','05','项目中介','06','待审核','未知') status, valid, create_time, yyyqsm from t_xm where valid = 1 and status != '01' and status != '06' and status != '07'
and(
xmmc like #{xmmcLike}
or xmzm like #{xmzmLike} )
and xmkssj >= #{xmkssj,jdbcType=TIMESTAMP}
and smjssj <= #{smjssj,jdbcType=TIMESTAMP}
